import React, { useState } from 'react';
import './index.css';

interface SplitBubbleProps {
  size: number
  initState: number 
}

const SplitBubble = (props: SplitBubbleProps) => {
  const { size, initState } = props
  const [isMyBubbleSpplitted, setIsMyBubbleSplitted] = useState(false)
  console.log(size, initState)

  return (
    <div onMouseOver={() => (setIsMyBubbleSplitted(true))} className="flex justify-center items-center">
      { isMyBubbleSpplitted ?
        // <div className={`w-[${size.toString()}px] h-[${size.toString()}px] grid-rows-2`}>
          <SplitBubble size={size / 2} initState={initState + 1}/>
          // <div className={`w-[${size.toString()}px] h-[${size.toString()}px] bg- rounded-full`} />
          // <SplitBubble size={size / 2} initState={false}/>
          // <SplitBubble size={size / 2} initState={false}/>
          // <SplitBubble size={size / 2} initState={false}/>
        // </div>
          :
        <div className={`w-[${size.toString()}px] h-[${size.toString()}px] bg-[#FF0000] rounded-full`} />
      }
    </div>
  )
}

function App() {
  return (
    <div className="w-screen min-h-screen h-screen">
      <div className="flex justify-center items-center text-red w-full h-full">
        <SplitBubble size={500} initState={1}/>
      </div>
    </div>
  );
}

export default App;
