import React, { useState } from 'react';
import './index.css';

interface SplitBubbleProps {
  size: number
  initState: number 
}

const SplitBubble = (props: SplitBubbleProps) => {
  const { size, initState } = props
  const [isMyBubbleSpplitted, setIsMyBubbleSplitted] = useState(false)

  return (
    <div onClick={() => {if (!isMyBubbleSpplitted) setIsMyBubbleSplitted(true)}} className="flex justify-center items-center w-full h-full">
      { isMyBubbleSpplitted ?
        // <div style={{width: `${size.toString()}px`, height: `${size.toString()}px`, display: "flex", flexDirection: "column"}}>
        <div className='flex flex-col w-1/2 h-1/2 bg-black rounded-full'>
          <div className='flex flex-row justify-between w-full h-full'>
            <SplitBubble size={size / 2} initState={initState + 1}/>
            <SplitBubble size={size / 2} initState={initState + 1}/>
          </div>
          <div className='flex flex-row justify-between w-full h-full'>
            <SplitBubble size={size / 2} initState={initState + 1}/>
            <SplitBubble size={size / 2} initState={initState + 1}/>
          </div>
        </div>
          :
        <div className='flex w-1/2 h-1/2 bg-black rounded-full'/>
      }
    </div>
  )
}

function App() {
  return (
    <div className="w-screen min-h-screen h-screen">
      <div className="flex justify-center items-center text-red w-96 h-96">
        <SplitBubble size={800} initState={1}/>
      </div>
    </div>
  );
}

export default App;
