type LineStepProps = {
  currentStep: number
  idx: number
}

const LineStep = (props: LineStepProps) => {
  const { currentStep, idx } = props
  const active = currentStep >= idx

  return <div className={`grow h-[2px] ${active ? "bg-grey-200" : "bg-green-200"}`} />
}

type BubbleStepProps = {
  stepName: string
  currentStep: number
  setStep?: (value: number) => void
  idx: number
}

const BubbleStep = (props: BubbleStepProps) => {
  const { stepName, currentStep, idx, setStep } = props
  const active = currentStep >= idx
  const bubbleIsCompletedStep = currentStep > idx

  return (
    <div className="relative">
      <div
        className={`flex justify-center items-center w-[20px] h-[20px] rounded-full border-solid border-[2px] ${
          active ? "border-primary-300" : "border-primary-200"
        } ${bubbleIsCompletedStep ? "bg-primary-300" : ""}`}
      >
        {bubbleIsCompletedStep &&
          (setStep ? (
            <button className="p-0" onClick={() => setStep(idx)}>
              <></>
              {/* <IconUI name="Path" width={12} height={12} color="white" /> */}
            </button>
          ) : (
            <></>
            // <IconUI name="Path" width={12} height={12} color="white" />
          ))}
      </div>
      <div className="absolute w-28 flex justify-center left-[-2.6rem] top-8">
        <div className="whitespace-nowrap">
          {stepName}
        </div>
      </div>
    </div>
  )
}

type StatusBarProps = {
  currentStep: number
  stepsName: Array<string>
  setStep?: (value: number) => void
}

const StatusBar = (props: StatusBarProps) => {
  const { currentStep, stepsName, setStep } = props

  return (
    <div className="w-full flex flex-row items-center px-24 h-[70px]">
      {stepsName.map((name, idx) => {
        if (idx === 0 || idx === stepsName.length) {
          return <BubbleStep stepName={name} setStep={setStep} currentStep={currentStep} idx={idx} />
        } else {
          return (
            <>
              <LineStep currentStep={currentStep} idx={idx} />
              <BubbleStep stepName={name} setStep={setStep} currentStep={currentStep} idx={idx} />
            </>
          )
        }
      })}
    </div>
  )
}

export default StatusBar
