import React, { useState } from "react";
import "./index.css";
import StatusBar from "./StatusBar";

function App() {
  const [step, setStep] = useState(2)
  const updateStep = (update: number) => {
    setStep((prev) => {
      if (update + prev > 4 || update + prev < 0) return prev
      return prev + update
    })
  }

  return (
    <div className="pt-4 px-16">
      <div className="h-[100px]">
        <StatusBar
          currentStep={step}
          setStep={setStep}
          stepsName={["Screening interview", "Tecknicall interview", "Fit interview", "Contract Negociation"]}
        />
      </div>
      <div className="flex flex-row gap-4 justify-end pt-4"> 
        <button onClick={() => {updateStep(-1)}} disabled={step === 0} className="p-2 px-4 underline rounded-xl disabled:opacity-40">Return</button>
        <button onClick={() => {updateStep(1)}} disabled={step === 4} className="p-2 px-4 bg-blue-400 rounded-xl text-white disabled:opacity-40">Next</button>
      </div>
    </div>
  );
}

export default App;
