import React, { useState } from 'react';
import './index.css';

interface SplitBubbleProps {
}

const SplitBubble = (props: SplitBubbleProps) => {
  const [isMyBubbleSpplitted, setIsMyBubbleSplitted] = useState(false)

  return (
    <div className="flex justify-center items-center w-full h-full">
      { isMyBubbleSpplitted ?
        <div className='flex flex-col w-full h-full rounded-full'>
          <div className='flex flex-row justify-between w-full h-full'>
            <SplitBubble />
            <SplitBubble />
          </div>
          <div className='flex flex-row justify-between w-full h-full'>
            <SplitBubble />
            <SplitBubble />
          </div>
        </div>
          :
        <div onClick={() => {if (!isMyBubbleSpplitted) setIsMyBubbleSplitted(true)}} className='flex w-1/2 h-1/2 bg-black rounded-full'/>
      }
    </div>
  )
}

function App() {
  return (
    <div className="w-screen min-h-screen h-screen">
      <div className="flex justify-center items-center w-full h-full">
        <div className="flex justify-center items-center h-[600px] aspect-square">
          <SplitBubble />
        </div>
      </div>
    </div>
  );
}

export default App;
