import React, { useRef, useState } from "react";
import "./index.css";

interface SplitBubbleProps {
  color: string
}

const SplitBubble = (props: SplitBubbleProps) => {
  const { color } = props
  const [isMyBubbleSpplitted, setIsMyBubbleSplitted] = useState(false);

  console.log("color", color)

  return (
    <div className="flex justify-center items-center w-full h-full">
      {isMyBubbleSpplitted ? (
        <div className="flex flex-col w-full h-full rounded-full">
          <div className="flex flex-row justify-between w-full h-full">
            <SplitBubble color={color} />
            <SplitBubble color={color} />
          </div>
          <div className="flex flex-row justify-between w-full h-full">
            <SplitBubble color={color} />
            <SplitBubble color={color} />
          </div>
        </div>
      ) : (
        <div
          style={{backgroundColor: color}}
          onClick={() => {
            if (!isMyBubbleSpplitted) setIsMyBubbleSplitted(true);
          }}
          className={`flex w-1/2 h-1/2  rounded-full`}
        />
      )}
    </div>
  );
};

function App() {
  const [color, setColor] = useState("#FF0000")
  const inputValue = useRef("#000000");

  return (
    <div className="w-screen min-h-screen h-screen flex flex-row bg-[#E5E5E5]">
      <div className="flex justify-center items-center w-1/5 h-full p-4 min-w-fit">
        <div className="flex flex-col justify-start items-center p-8 h-full bg-white drop-shadow-md rounded-xl w-96">
          <div className="text-xl pb-8">
            This is a little form
          </div>
          <form id="hexacode" onSubmit={(event) => {event.preventDefault(); setColor(inputValue.current.value);}}>
            <label htmlFor="hexacode">Enter an hexacode color</label>
            <input ref={inputValue} type="text" id="hexacode" name="hexacode" required />
            <button form="hexacode" type="submit">Confirm</button>
          </form>
        </div>
      </div>
      <div className="flex justify-center items-center w-4/5 h-full">
        <div className="flex justify-center items-center w-2/3 aspect-square bg-white drop-shadow-md rounded-xl">
          <SplitBubble color={color} />
        </div>
      </div>
    </div>
  );
}

export default App;
