import React, { useState } from 'react';
import logo from './logo.svg';
import './App.css';

interface SplitBubbleProps {
  size: number
}

const SplitBubble = (props: SplitBubbleProps) => {
  const { size } = props
  const [isMyBubbleSpplitted, setIsMyBubbleSplitted] = useState(false)

  return (
    <div onMouseOver={() => (setIsMyBubbleSplitted(true))}>
      { isMyBubbleSpplitted ?
        <div className={`w-[${size.toString()}] h-[${size.toString()}] grid-rows-2 `}>
          <SplitBubble size={size / 2}/>
          <SplitBubble size={size / 2}/>
          <SplitBubble size={size / 2}/>
          <SplitBubble size={size / 2}/>
        </div>
          :
        <div className={`w-[${size.toString()}] h-[${size.toString()}] bg-color-red radius-rounded`} />
      }
    </div>
  )
}

function App() {
  return (
    <div className="flex justify-center items-center w-screen h-screen">
      <SplitBubble size={500}/>
    </div>
  );
}

export default App;
